sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var message = '';

    // データ検索（JOINは難しいとのことなので個別に該当レコードを検索する方向にしてみる）
	var grD = new GlideRecord('x_58872_needit_mydatatable');
	var grS = grD.addJoinQuery('x_58872_needit_mysalary', 'salary', 'sys_id');
//	grS.addCondition('salary', '>=', '400');
	grD.orderBy('id');
	grD.query();

	var count = 0;
	while (grD.next()) {
		var info = '';
		if (count != 0) {
			info = '\n';
		}
		info = info + '#' + grD.getValue('id') + '\t'; // Id
		info = info + grD.getElement('name').getLabel() + ':\[' + grD.getValue('name') + '\], ';   // 名前
		info = info + grD.getElement('age').getLabel() +':\[' + grD.getValue('age') + '\], ';     // 年齢
		info = info + grD.getElement('description').getLabel() + ':\[' + grD.getValue('description') + '\], '; // 説明
		info = info + grD.getElement('remarks').getLabel() + ':\[' + grD.getValue('remarks') + '\], '; // 備考

		// 給料情報をIDで検索する
		info = info + grD.getElement('salary').getLabel() + ':\['
			+ grD.getElement('salary').salary + '\]'; // 給料

		message = message + info;
		count++;
	}

	// Result 更新
	gr = new GlideRecord('x_58872_needit_myaggregatetable');
	gr.addQuery('sys_id', '8587c8fb4f972700ca928fe18110c7d6');
	gr.query();
	while (gr.next()) {
		gr.result = message;
		gr.update();
	}
})(current, previous);
