sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var message = '';

    // データ検索（JOINは難しいとのことなので個別に該当レコードを検索する方向にしてみる）
	grD = new GlideRecord("x_58872_needit_mydatatable");
	grD.orderBy('id');
	grD.query();

	if (grD.hasNext()) {
		message = message + 'データの存在を確認。\n';
	}

	var count = 0;
	while (grD.next()) {
		var info = '';
		if (count != 0) {
			info = '\n';
		}
		info = info + '#' + grD.getValue('id') + '\t'; // Id
		info = info + grD.getElement('name').getLabel() + ':\[' + grD.getValue('name') + '\], ';   // 名前
		info = info + grD.getElement('age').getLabel() +':\[' + grD.getValue('age') + '\], ';     // 年齢
		info = info + grD.getElement('description').getLabel() + ':\[' + grD.getValue('description') + '\], '; // 説明
		info = info + grD.getElement('remarks').getLabel() + ':\[' + grD.getValue('remarks') + '\], '; // 備考

		// 給料情報をIDで検索する
		var salary = getSalary(grD.getValue('id'));
		info = info + salary.getLabel() + ':\[' + salary + '\]'; // 給料

		message = message + info;
		count++;
	}

	// Result 更新
	gr = new GlideRecord('x_58872_needit_myaggregatetable');
	gr.addQuery('sys_id', 'a85940154fd32300ca928fe18110c748');
	gr.query();
	while (gr.next()) {
		gr.result = message;
		gr.update();
	}
})(current, previous);

// 給料情報取得
function getSalary(id) {
	// 給料情報をIDで検索する
	var grS = new GlideRecord("x_58872_needit_mysalary");
	grS.addQuery('id', id);
	grS.query();
	grS.next();
	return grS.getElement('salary'); // 給料
}
